function distance(a, b) {
	return Math.sqrt((a.x - b.x) ** 2 + (a.y - b.y) ** 2);
}

const planets = runtime.objects['Planet'].getAllInstances();
const ship = runtime.objects['Ship'].getFirstInstance();

let accelX = 0;
let accelY = 0;

//if (ship.instVars.orbiting) {
//} else {
for (const planet of planets) {
	const dist = distance(planet, ship);
	// apply gravity from planet to ship
	let force = 0;
	if (dist > runtime.globalVars.minGravityDistance) {
		force = runtime.globalVars.G / (dist * dist);
	} else {
		const peak = runtime.globalVars.G / (runtime.globalVars.minGravityDistance ** 2);
		force = (peak / runtime.globalVars.minGravityDistance) * dist;
	}

	accelX += force * (planet.x - ship.x) / dist;
	accelY += force * (planet.y - ship.y) / dist;
}

// accelerate
ship.instVars.dx += accelX * runtime.dt;
ship.instVars.dy += accelY * runtime.dt;

// move
ship.x += ship.instVars.dx * runtime.dt;
ship.y += ship.instVars.dy * runtime.dt;

