function distance(a, b) {
	return Math.sqrt((a.x - b.x) ** 2 + (a.y - b.y) ** 2);
}

const planets = runtime.objects['Planet'].getAllInstances();
const ship = runtime.objects['Ship'].getFirstInstance();

if (ship.instVars.orbiting) {
	ship.instVars.orbiting = false;
	ship.instVars.dx 
}
let closestPlanet = planets[0], closestDistance = distance(closestPlanet, ship);
for (let i = 1; i < planets.length; i += 1) {
	const thisPlanetDistance = distance(planets[i], ship);
	if (thisPlanetDistance < closestDistance) {
		closestPlanet = planets[i];
		closestDistance = thisPlanetDistance;
	}
}

if (closestDistance < runtime.globalVars.maxOrbitDistance) {

	ship.instVars.orbiting = true;
	ship.instVars.orbitedPlanet = closestPlanet.uid;

	// compute vector from ship to planet
	const shipToPlanet = [closestPlanet.x - ship.x, closestPlanet.y - ship.y];
	console.log(shipToPlanet);
	// normalize it
	shipToPlanet[0] /= closestDistance;
	shipToPlanet[1] /= closestDistance;
	console.log(shipToPlanet);

	// figure out if we should orbit CW or CCW
	// P = (x2 - x1)(y3 - y1) - (y2 - y1)(x3 - x1)
	// 1 = planet
	// 2 = current ship
	// 3 = next ship
	const cross = (ship.x - closestPlanet.x) * (ship.y + ship.instVars.dy - closestPlanet.y)
		- (ship.y - closestPlanet.y) * (ship.x + ship.instVars.dx - closestPlanet.x);
	const counterclockwise = cross < 0;

	// rotate 90 degrees CW
	const orbitDirection = (counterclockwise
		? [-shipToPlanet[1], shipToPlanet[0]]
		: [shipToPlanet[1], -shipToPlanet[0]]);
	const orbitVelocity = Math.sqrt(runtime.globalVars.G * 1 / closestDistance);
	console.log(orbitVelocity);
	const orbitVec = [orbitDirection[0] * orbitVelocity, orbitDirection[1] * orbitVelocity];
	const dv_x = orbitVec[0] - ship.instVars.dx;
	const dv_y = orbitVec[1] - ship.instVars.dy;
	ship.instVars.dv = Math.sqrt(dv_x ** 2 + dv_y ** 2);
	ship.instVars.dx = orbitVec[0];
	ship.instVars.dy = orbitVec[1];
}

const ship = runtime.objects['Ship'].getFirstInstance();
if (ship.instVars.orbiting) {
	ship.instVars.orbiting = false;
	const currentVel = Math.sqrt(ship.instVars.dx ** 2 + ship.instVars.dy ** 2);
	const factor = (currentVel + ship.instVars.dv) / currentVel;
	ship.instVars.dx *= factor;
	ship.instVars.dy *= factor;
}
